import "./Registration.css";
import React from "react";

function template() {
  const {name,location,myclass}=this.state.data; 

  return (
  <div className="registration container-fluid mb-5">
  <h1 className="text-center mb-5">---------Registration---------</h1>
  <div className="column form-group">
  <div className="col-7 text-right">Enter Name:<input value={name} id="name" onChange={this.fn.bind(this)}/></div>
  <div className="col-7 text-right">Enter Class:<input value={myclass} id="myclass" onChange={this.fn.bind(this)}/></div>
  <div className="col-7 text-right">Enter Loc:<input value={location} id="location" onChange={this.fn.bind(this)}/></div>
  </div>
  <div className="col-7 text-right"><button className="btn-primary" onClick={this.fnreg.bind(this)}>Register</button></div>
  </div>
  );
};

export default template;
