import "./Registration.css";
import React from "react";

function template() {
  const {name,location,myclass}=this.state.data; 

  return (

    <div className="registration comtainer-fluid mb-5">
      <h1 className="text-center mb-5">---------Registration---------</h1>
      <div className="row form-group">
        <div className="col-5 text-right">Enter Name:<input value={name} id="name" onChange={this.fn.bind(this)}/></div>
         <div className="col-5 text-right">Enter Class:<input value={myclass}id="class" onChange={this.fn.bind(this)}/></div>
         <div className="col-5 text-right">Enter Loc:<input value={location} id="location" onChange={this.fn.bind(this)}/></div>
         </div>
      <div className="text center mb-1"><button className="btn-primary">Register</button></div>
    </div>
  );
};

export default template;
